record.field:
# Lấy patient từ context
patient_id = env.context.get('patient_id')
service_id = env.context.get('default_service_id')

if patient_id and service_id:
    patient = env['res.partner'].browse(patient_id)
    if patient.exists() and patient.is_patient:
        action = patient.with_context(target_service_id=service_id).action_swap_to_service()
    else:
        action = {
            'type': 'ir.actions.client',
            'tag': 'display_notification',
            'params': {
                'title': 'Lỗi',
                'message': 'Không tìm thấy bệnh nhân hợp lệ',
                'type': 'danger',
                'sticky': False
            }
        }
else:
    action = {
        'type': 'ir.actions.client',
        'tag': 'display_notification',
        'params': {
            'title': 'Lỗi',
            'message': 'Thiếu thông tin bệnh nhân hoặc dịch vụ',
            'type': 'danger',
            'sticky': False
        }
    }
